import algosdk from "algosdk";

const port = "";
const CHOICE_ID = 21364625;
const baseServer = "https://testnet-algorand.api.purestake.io/ps2";
const indexerAddress = "https://testnet-algorand.api.purestake.io/idx2";
const token = { "X-API-Key": "fi0QdbiBVl8hsVMCA2SUg6jnQdvAzxY48Zy2G6Yc" };
const algodClient = new algosdk.Algodv2(token, baseServer, port);

const indexerClient = new algosdk.Indexer(token, indexerAddress, port);

const getCommittedAmount = async () => {
  const options = [
    {
      address: "DQ2NPLGQ3CV3QAPMS7KHIRUNUF2ZSTYRT5SZGDCPLB7VPC5OSSLCI5H7DM",
      image: "",
      name: "Yes",
    },

    {
      address: "6P6D5KQH5VIXEJGW6LXPUMEZ77XNLHQXB6GMW4CBDBN2VJ4CFZ7HCIKUBM",
      image: "",
      name: "No",
    },
  ];

  const firstAddress = options[0].address;
  const secondAddress = options[1].address;

  try {
    const txnArray = [];
    const userAddress =
      "S76LPUPTSGL56BUGRKEQSWEDQ6VSMP62FPE3XJDREJ2XIDWQC36DL2KDJQ";

    let pastTxn = await indexerClient
      .searchForTransactions()
      .address(userAddress)
      .assetID(CHOICE_ID)
      .limit(20)
      .do();

    const txns = pastTxn["transactions"];

    txns.forEach((element) => {
      let obj = {};
      const txn = element["asset-transfer-transaction"];

      if (txn["receiver"] == firstAddress) {
        obj = {
          candidate: options[0].name,
          address: firstAddress,
          Id: element["id"],
          amount: txn["amount"] / 100,
        };

        txnArray.push(obj);
      } else if (txn["receiver"] == secondAddress) {
        obj = {
          Id: element["id"],
          address: secondAddress,
          candidate: options[0].name,
          amount: txn["amount"] / 100,
        };
        txnArray.push(obj);
      }
    });
    console.log(txnArray);
  } catch (error) {
    console.log(error);
  }

  return "good";
};

export { getCommittedAmount };
