import algosdk from "algosdk";

const port = "";
const CHOICE_ID = 21364625;
const baseServer = "https://testnet-algorand.api.purestake.io/ps2";
const indexerAddress = "https://testnet-algorand.api.purestake.io/idx2";
const token = { "X-API-Key": "fi0QdbiBVl8hsVMCA2SUg6jnQdvAzxY48Zy2G6Yc" };

const algodClient = new algosdk.Algodv2(token, baseServer, port);

const indexerClient = new algosdk.Indexer(token, indexerAddress, port);

const getTotal = async () => {
  try {
    let txnArray = [];
    let userAddress =
      "S76LPUPTSGL56BUGRKEQSWEDQ6VSMP62FPE3XJDREJ2XIDWQC36DL2KDJQ";
    let history = await indexerClient
      .searchForTransactions()
      .address(userAddress)
      .assetID(CHOICE_ID)
      .limit(20)
      .do();

    const txn = history["transactions"];
    //loop to check if the receiver was red_address or blue address
    transactions_history.forEach((element) => {
      const transaction = element["asset-transfer-transaction"];
      if (transaction["receiver"] == red_address) {
        obj = {
          candidate: "Red",
          address: red_address,
          Id: element["id"],
          amount: transaction["amount"] / 100,
        };
        txnArray.push(obj);
      } else if (transaction["receiver"] == blue_address) {
        obj = {
          candidate: "Blue",
          address: blue_address,
          Id: element["id"],
          amount: transaction["amount"] / 100,
        };
        txnArray.push(obj);
      }
    });
    console.log(txnArray);
  } catch (error) {
    console.log(error);
  }
};

const getCommittedAmount = async () => {
  const history = await indexerClient
    .searchForTransactions()
    .address("S76LPUPTSGL56BUGRKEQSWEDQ6VSMP62FPE3XJDREJ2XIDWQC36DL2KDJQ")
    .assetID(CHOICE_ID)
    .limit(20)
    .do();

  const txn = history["transactions"];

  return txn;
};

export { getCommittedAmount };
