import axios from "axios";
import "../styles/landing.css";
import { URL } from "../constants";
import { useQuery } from "react-query";
import loadable from "@loadable/component";
const ScrollTextLand = loadable(() => import("../components/ScrollTextLand"));

const Landing = () => {
  //

  const walletAddress = localStorage.getItem("address");

  const { isLoading, error, data } = useQuery("committed", () =>
    axios
      .get(`${URL}/committed/${walletAddress}`)
      .then((response) => response.data.data)
  );

  return (
    <div className="landing" id="landing">
      {walletAddress && (
        <div
          style={{
            width: "100%",
            height: "60px",
            display: "flex",
            fontSize: "13px",
            fontWeight: "500",
            wordSpacing: "4px",
            alignItems: "center",
            color: "var(--wht)",
            letterSpacing: "0.5px",
            textTransform: "uppercase",
            borderBottom: "1px solid var(--border-default)",
          }}
        >
          <p style={{ opacity: "0.6" }}>
            Amount committed to Governance:{" "}
            {!isLoading && !error && data.amount} $Choice
          </p>
        </div>
      )}
    </div>
  );
};

export default Landing;
