import { useDispatch } from "react-redux";

const BottomNavigationBar = ({ NavLink, darkTheme }) => {
  const dispatch = useDispatch();

  const LogOut = () => {
    localStorage.clear();
    window.location.reload();
    console.log("data");
  };

  const setMode = () => {
    if (!darkTheme) {
      localStorage.setItem("mode", "dark");
      dispatch({ type: "dark_mode" });
    } else {
      localStorage.setItem("mode", "light");
      dispatch({ type: "light_mode" });
    }
  };

  return (
    <footer className="ft_sm">
      <ul className="ft_sm_inn">
        <li className="ft_sm_li">
          <NavLink
            style={({ isActive }) => {
              return {
                display: "flex",
                fontSize: isActive ? "14px" : "13px",
                fontWeight: isActive && "600",
                opacity: isActive ? "1" : "0.6",
                alignItems: "center",
                flexDirection: "column",
              };
            }}
            to={`/`}
            key={"home"}
          >
            <i className="uil uil-estate"></i>
            {/* <p> Home</p> */}
          </NavLink>
        </li>
        <li className="ft_sm_li">
          <NavLink
            style={({ isActive }) => {
              return {
                display: "flex",
                fontSize: isActive ? "14px" : "13px",
                fontWeight: isActive && "600",
                opacity: isActive ? "1" : "0.6",
                alignItems: "center",
                flexDirection: "column",
              };
            }}
            to={`/elections`}
            key={"elections"}
          >
            <i class="uil uil-mailbox"></i>
          </NavLink>
        </li>

        {/* Transfer Section */}
        <li className="ft_sm_li">
          <NavLink
            style={({ isActive }) => {
              return {
                width: "35px",
                height: "35px",
                display: "flex",
                borderRadius: "100%",
                alignItems: "center",
                justifyContent: "center",
                fontWeight: isActive && "600",
                background: "var(--main-col)",
                color: !darkTheme ? "#fff" : "",
                border: isActive
                  ? darkTheme
                    ? "1px solid var(--wht)"
                    : "1px solid #666"
                  : "none",
                fontSize: isActive ? "14px" : "13px",
              };
            }}
            to={`/transfer`}
            key={"transfer"}
          >
            <i className="uil uil-exchange"></i>
          </NavLink>
        </li>

        <li className="ft_sm_li" onClick={setMode}>
          <div
            style={{
              display: "flex",
              fontSize: "13px",
              fontWeight: "600",
              opacity: "0.6",
              cursor: "pointer",
              alignItems: "center",
              flexDirection: "column",
            }}
          >
            <i className="uil uil-moon" style={{ fontSize: "21px" }}></i>
          </div>
        </li>

        <li className="ft_sm_li">
          <div
            style={{
              display: "flex",
              fontSize: "13px",
              fontWeight: "600",
              opacity: "0.6",
              cursor: "pointer",
              alignItems: "center",
              flexDirection: "column",
            }}
            onClick={LogOut}
          >
            <i className="uil uil-signout" style={{ fontSize: "21px" }}></i>
          </div>
        </li>
      </ul>
    </footer>
  );
};

export default BottomNavigationBar;
